import React, { useState } from 'react'
import Tasksection from '../components/TaskSection';
import {dotWhite, dotBlack, night, sun, sidebar, xMark, xMarkWHite, plus, minusBlack, minusWhite, plusWhite} from '../module.js'

// import Header from './Header'

type AllBoards = {
  id: number;
  name: string;
  icn: string;
}
interface Boxs {
  id: number;
  price: string;
  title: string;
  icn: string;
}

const sectionOne: Boxs[] = [
  {
    id: 1,
    title: 'Build UI or onboarding flow',
    price: '0 of 3 substaks',
    icn: plusWhite,
  },
  {
    id: 2,
    title: 'Build UI for search',
    price: '0 of 1 substaks',
    icn: plusWhite,
  },
  {
    id: 3,
    title: 'Build settings UI',
    price: '0 of 2 substaks',
    icn: plusWhite 
  },
]
const sectionTwo: Boxs[] = [
  {
    id: 1,
    title: 'Design setting and search pages',
    price: '3 of 1 substaks',
    icn: plusWhite 
  },
  {
    id: 2,
    title: 'Add accaount management endpoints',
    price: '3 of 2 substaks',
    icn: plusWhite 
  },
  {
    id: 3,
    title: 'Design onboarding flow',
    price: '3 of 3 substaks',
    icn: plusWhite 
  },
  {
    id: 4,
    title: 'Add authentication endpoints',
    price: '0 of 1 substaks',
    icn: plusWhite 
  },
]
const sectionThree: Boxs[] = [
  {
    id: 1,
    title: 'Create wireframe prototype',
    price: '2 of 1 substaks',
    icn: plusWhite 
  },
  {
    id: 2,
    title: 'Review results of usability tests',
    price: '2 of 2 substaks',
    icn: plusWhite 
  },
  {
    id: 3,
    title: 'Market discovery',
    price: '4 of 1 substaks',
    icn: plusWhite 
  },
  {
    id: 4,
    title: 'Research the market',
    price: '4 of 2 substaks',
    icn: plusWhite 
  },
  {
    id: 5,
    title: 'Conduct 5 wireframe tests',
    price: '1 of 1 substaks',
    icn: plusWhite 
  },
]

const allBoards: AllBoards[] = [ 
  {
    id: 1,
    name: 'platform launch',
    icn: sidebar
  },
  {
    id: 2,
    name: 'marketing plan',
    icn: sidebar
  },
  {
    id: 3,
    name: 'Roadmap',
    icn: sidebar
  },
  {
    id: 4,
    name: 'create new board',
    icn: sidebar
  }
]


export const Main = () => {
const [toggleNight, setToggleNight] = useState(false);
const [isModal, setIsModal] = useState(false);
// const [togglePlus, setTogglePlus] = useState(false);
const [togglePlus, setTogglePlus] = useState<{ [key: number]: boolean }>(
  sectionOne.reduce<{ [key: number]: boolean }>((acc, section) => {
    acc[section.id] = false;
    return acc;
  }, {})
);



function toggleSectionPlus(id: number) {
  setTogglePlus(prevState => ({
    ...prevState,
    [id]: !prevState[id]
  }));
}

function changeBackground () {
  setToggleNight(!toggleNight)
}


  return (
    <>
    <header className={`header-light ${toggleNight ? 'header-night' : ''}`}>
        <h2>platform launch</h2>
        <button onClick={() => setIsModal(!isModal)}>+ add new task</button>
        {toggleNight ? <img src={dotWhite}  /> : <img src={dotBlack}  />}
    </header>


<div className={`sunlight ${toggleNight ? 'moonlight' : ''}`}>
    <div className='box'>
        <div></div>
        <div></div>
        <div></div>
    </div>

  <h1> <div className='box'>
        <div className='div-1'></div>
        <div className='div-2'></div>
        <div className='div-3'></div>
    </div>kanban</h1>

    <div className='boards'>
      <h5>all boards (8)</h5>
      <ul>
        {allBoards.map(({id, icn, name}) => 
          <li 
            className='platform'
            key={id}>
            <img src={icn}/>
            <p>{name}</p>
          </li>
        )}
      </ul>
    </div>

<div className='toggle'>
  <img src={sun} />
    <button onClick={changeBackground} 
    className={`sun ${toggleNight ? 'moon' : false}`}><div></div></button>
  <img src={night} />
</div>
</div>


    <div className='main'>
        <div className='main-header'>
          <div>
            <div className='todo'></div><p style={{
              marginRight: '800px'
            }}>TODO (4)</p>
          </div>
