import React from 'react';
import '../styles/TaskSection.scss'

interface SubTask {
  title: string;
  progress: string;
  plus: string;
  plusWhite: string;
}

interface TaskSectionProps {
  title: string;
  subTitle: string;
  togglePlus: () => void;
  toggleNight: boolean;
  plusIcon: string; // Add this line
  minusIcon: string; // Add this line
  subTasks?: SubTask[];
}

const Tasksection: React.FC<TaskSectionProps> = ({
  title,
  subTitle,
  subTasks,
  togglePlus,
  toggleNight,
}) => {
  return (
    <section className='section'>
      <div>
        <h3>{title}</h3>
        <p>{subTitle}</p>
        {subTasks && subTasks.map((subTask, index) => (
          <div key={index}>
            <h4>{subTask.title}</h4>
            <p>{subTask.progress}</p>
            <img
              onClick={togglePlus}
              className='plus'
              src={toggleNight ? subTask.plusWhite : subTask.plus}
              alt="Toggle Plus"
            />
          </div>
        ))}
      </div>
    </section>
  );
};

export default Tasksection;
